mapper.select:
SELECT d.id as did,d.realname as drealname,d.position as dposition, d.title as dtitle,group_concat(DISTINCT concat(t.textbook_name,'-',(case when p.preset_position=0 OR p.preset_position IS NULL then '无' when p.preset_position = 4 then '主编' when p.preset_position = 2 then '副主编' when p.preset_position = 1 then '编委' when p.preset_position = 8 then '数字编委' when p.preset_position = 3 then '副主编,编委' when p.preset_position = 5 then '主编,编委' when p.preset_position = 6 then '主编,副主编' when p.preset_position = 7 then '主编,副主编,编委' when p.preset_position = 12 then '主编,数字编委' when p.preset_position = 9 then '数字编委,编委' when p.preset_position = 10 then '数字编委,副主编' when p.preset_position = 11 then '数字编委,编委,副主编' when p.preset_position = 13 then '数字编委,主编,编委' when p.preset_position = 14 then '数字编委,主编,副主编' when p.preset_position = 15 then '数字编委,主编,编委,副主编' else '无' end )) SEPARATOR '\n') as bpp, p.preset_position, t.textbook_name, (case when d.online_progress=0 then '未提交' when d.online_progress=1 then '未审核' when d.online_progress=2 then '已退回' when d.online_progress=3 then '已审核' when d.online_progress=4 then '未审核' when d.online_progress=5 then '已退回' END) as onlineprogress, (case when d.offline_progress=0 then '未收到纸质表' when d.offline_progress=1 then '' when d.offline_progress=2 then '已收到纸质表' END) as offlineprogress, group_concat(DISTINCT concat(t.textbook_name,'-',(case when m.is_force_end=1 and t.is_published=0 then '遴选结束' when t.is_published=0 then '遴选中' when pp.chosen_position=0 OR pp.chosen_position IS NULL then '遴选结束' when pp.chosen_position = 4 then '主编' when pp.chosen_position = 2 then '副主编' when pp.chosen_position = 1 then '编委' when pp.chosen_position = 8 then '数字编委' when pp.chosen_position = 12 then '主编,数字编委' when pp.chosen_position = 9 then '数字编委,编委' when pp.chosen_position = 10 then '数字编委,副主编' else '遴选结束' end )) SEPARATOR '\n') as cp, (case when d.isprint=0 then '未打印' when d.isprint=1 then '已打印' END) as isprint FROM declaration AS d LEFT JOIN material m ON d.material_id=m.id LEFT JOIN dec_position AS p ON d.id = p.declaration_id LEFT JOIN dec_position_published AS pp ON d.id = pp.declaration_id LEFT JOIN textbook AS t ON p.textbook_id = t.id LEFT JOIN org as o on d.org_id=o.id INNER JOIN org_user as ou on d.org_id=ou.org_id and d.online_progress !=0
1=1
and d.realname like concat("%",#{parameter.realname},"%")
and t.textbook_name like concat("%",#{parameter.textbook_name},"%")
and d.title like concat("%",#{parameter.title},"%")
and d.online_progress in (${parameter.online_progress})
and d.material_id = ${parameter.material_id}
and ou.id = ${parameter.userId}
GROUP BY d.id limit ${start},${pageSize}

		select t1.*,t2.org_name dwmc from (select id,material_id,user_id,realname,sex,REPLACE(birthday,'-','.') as birthday,experience,org_name,position,title,address,postcode,handphone,email,idtype,
idcard,telephone,fax,org_id,online_progress,auth_user_id,auth_date,offline_progress,paper_date,is_staging*1 is_staging,is_deleted*1 is_deleted,
gmt_create,gmt_update,is_dispensed*1 is_dispensed,is_utec*1 is_utec,degree,expertise,return_cause from declaration) t1 left join (select id,org_name from org 
	union select '0' id,'人民卫生出版社' org_name from dual) t2 on t1.org_id = t2.id
		 where 1= 1 
		
			and t1.user_id = #{user_id} 
		
		
			and t1.material_id = #{material_id}
		
		
			and t1.id = #{declaration_id}
		
		
			and t1.realname = #{realname}
