mapper.select:
SELECT d.id, d.realname, d.position, d.title, t.textbook_name, p.preset_position, d.online_progress, d.offline_progress, p.chosen_position FROM declaration AS d LEFT JOIN dec_position AS p ON d.id = p.declaration_id LEFT JOIN textbook AS t ON p.textbook_id = t.id LEFT JOIN org as o on d.org_id=o.id INNER JOIN org_user as ou on d.org_id=ou.org_id and d.online_progress!=0
1=1
and d.realname like concat("%",#{queryName},"%")
and d.material_id = ${material_id}
and ou.id = ${userId}
limit ${startNum},${pageSize}

SELECT COUNT(*) FROM declaration AS d LEFT JOIN dec_position AS p ON d.id = p.declaration_id LEFT JOIN textbook AS t ON p.textbook_id = t.id LEFT JOIN org as o on d.org_id=o.id INNER JOIN org_user as ou on d.org_id=ou.org_id and d.online_progress!=0
1=1
and d.realname like concat("%",#{queryName},"%")
and d.material_id = ${material_id}
and ou.id = ${userId}

SELECT d.id, d.realname, d.position, d.title, t.textbook_name, p.preset_position, d.online_progress, d.offline_progress, p.chosen_position FROM declaration AS d LEFT JOIN dec_position AS p ON d.id = p.declaration_id LEFT JOIN textbook AS t ON p.textbook_id = t.id LEFT JOIN org as o on d.org_id=o.id INNER JOIN org_user as ou on d.org_id=ou.org_id and d.online_progress!=0
1=1
and d.realname like concat("%",#{queryName},"%")
and d.material_id = ${material_id}
and ou.id = ${userId}

select t1.*,t2.org_name dwmc from declaration t1 left join org t2 on t1.org_id = t2.id
 where 1= 1 

	and t1.user_id = #{user_id} 


	and t1.material_id = #{material_id}


	and t1.id = #{declaration_id}


	and t1.realname = #{realname}

select t1.*,t2.textbook_name from dec_position t1 left join textbook t2
on t1.textbook_id = t2.id where t1.declaration_id = #{declaration_id} 
ORDER BY t1.gmt_create desc
