mapper.select:
select t.textbook_name, sum(case when p.preset_position & 4 >0 then 1 else 0 end) as decid1, sum(case when p.preset_position & 2 >0 then 1 else 0 end) as decid2, sum(case when p.preset_position & 1 >0 then 1 else 0 end) as decid3, sum(case when p.preset_position & 8 >0 then 1 else 0 end) as decid4, sum(case when dp.chosen_position & 4 >0 then 1 else 0 end) as dp1, sum(case when dp.chosen_position & 2 >0 then 1 else 0 end) as dp2, sum(case when dp.chosen_position & 1 >0 then 1 else 0 end) as dp3, sum(case when dp.chosen_position & 8 >0 then 1 else 0 end) as dp4 from textbook t left join declaration as d on d.material_id = t.material_id left JOIN org_user as ou on d.org_id=ou.org_id LEFT JOIN dec_position_published dp ON t.id=dp.textbook_id AND dp.declaration_id=d.id LEFT JOIN dec_position p ON t.id=p.textbook_id and p.declaration_id=d.id
1=1
and t.material_id = ${material_id}
and ou.id=${userId}
GROUP BY t.id HAVING (decid1>=1 or decid2>=1 or decid3>=1 or decid4>=1)

select t.textbook_name, sum(case when p.preset_position & 1 >0 then 1 else 0 end) as decid1, sum(case when p.preset_position & 2 >0 then 1 else 0 end) as decid2, sum(case when p.preset_position & 3 >0 then 1 else 0 end) as decid3, sum(case when p.preset_position & 4 >0 then 1 else 0 end) as decid4, sum(case when dp.chosen_position & 1 >0 then 1 else 0 end) as dp1, sum(case when dp.chosen_position & 2 >0 then 1 else 0 end) as dp2, sum(case when dp.chosen_position & 3 >0 then 1 else 0 end) as dp3, sum(case when dp.chosen_position & 4 >0 then 1 else 0 end) as dp4 from textbook t left join declaration as d on d.material_id = t.material_id left JOIN org_user as ou on d.org_id=ou.org_id LEFT JOIN dec_position_published dp ON t.id=dp.textbook_id AND dp.declaration_id=d.id LEFT JOIN dec_position p ON t.id=p.textbook_id and p.declaration_id=d.id
1=1
and t.material_id = ${material_id}
and ou.id=${userId}
GROUP BY t.id HAVING (decid1>=1 or decid2>=1 or decid3>=1 or decid4>=1)

select t.textbook_name, sum(case when p.preset_position & 4 >0 then 1 else 0 end) as decid1, sum(case when p.preset_position & 2 >0 then 1 else 0 end) as decid2, sum(case when p.preset_position & 1 >0 then 1 else 0 end) as decid3, sum(case when p.preset_position & 8 >0 then 1 else 0 end) as decid4, sum(case when dp.chosen_position & 4 >0 then 1 else 0 end) as dp1, sum(case when dp.chosen_position & 2 >0 then 1 else 0 end) as dp2, sum(case when dp.chosen_position & 1 >0 then 1 else 0 end) as dp3, sum(case when dp.chosen_position & 8 >0 then 1 else 0 end) as dp4 from textbook t left join declaration as d on d.material_id = t.material_id left JOIN org_user as ou on d.org_id=ou.org_id LEFT JOIN dec_position_published dp ON t.id=dp.textbook_id AND dp.declaration_id=d.id LEFT JOIN dec_position p ON t.id=p.textbook_id and p.declaration_id=d.id
1=1
and t.material_id = ${material_id}
and ou.id=${userId}
GROUP BY t.id

select t.textbook_name, sum(case when p.preset_position & 1 >0 then 1 else 0 end) as decid1, sum(case when p.preset_position & 2 >0 then 1 else 0 end) as decid2, sum(case when p.preset_position & 3 >0 then 1 else 0 end) as decid3, sum(case when p.preset_position & 4 >0 then 1 else 0 end) as decid4, sum(case when dp.chosen_position & 1 >0 then 1 else 0 end) as dp1, sum(case when dp.chosen_position & 2 >0 then 1 else 0 end) as dp2, sum(case when dp.chosen_position & 3 >0 then 1 else 0 end) as dp3, sum(case when dp.chosen_position & 4 >0 then 1 else 0 end) as dp4 from textbook t left join declaration as d on d.material_id = t.material_id left JOIN org_user as ou on d.org_id=ou.org_id LEFT JOIN dec_position_published dp ON t.id=dp.textbook_id AND dp.declaration_id=d.id LEFT JOIN dec_position p ON t.id=p.textbook_id and p.declaration_id=d.id
1=1
and t.material_id = ${material_id}
and ou.id=${userId}
GROUP BY t.id

select t.*,COUNT(zb) as zb,COUNT(fb) as fb,COUNT(bw) as bw ,COUNT(szbw) as szbw from ( SELECT t.id, t.textbook_name, GROUP_CONCAT( ( case when dp.chosen_position & 4 >0 then d.realname END ) ) AS zb, GROUP_CONCAT( ( case when dp.chosen_position & 2 >0 then d.realname END ) ) AS fb, GROUP_CONCAT( ( case when dp.chosen_position & 1 >0 then d.realname END ) ) AS bw, GROUP_CONCAT( ( case when dp.chosen_position & 8 >0 then d.realname END ) ) AS szbw from textbook t left join declaration as d on d.material_id = t.material_id left JOIN org_user as ou on d.org_id=ou.org_id LEFT JOIN dec_position_published dp ON t.id=dp.textbook_id and dp.declaration_id=d.id
1=1
and t.material_id = ${material_id}
and ou.id=${userId}
GROUP BY t.id ) t GROUP BY t.textbook_name HAVING zb>=1 or fb>=1 or bw>=1 or szbw>=1

SELECT t.id, t.textbook_name, GROUP_CONCAT( ( case when dp.chosen_position & 4 >0 then d.realname END ) ) AS zb, GROUP_CONCAT( ( case when dp.chosen_position & 2 >0 then d.realname END ) ) AS fb, GROUP_CONCAT( ( case when dp.chosen_position & 1 >0 then d.realname END ) ) AS bw, GROUP_CONCAT( ( case when dp.chosen_position & 8 >0 then d.realname END ) ) AS szbw from textbook t left join declaration as d on d.material_id = t.material_id left JOIN org_user as ou on d.org_id=ou.org_id LEFT JOIN dec_position_published dp ON t.id=dp.textbook_id and dp.declaration_id=d.id
1=1
and t.material_id = ${material_id}
and ou.id=${userId}
GROUP BY t.id
