mapper.select:
select e.id,e.user_id,e.org_id,e.product_id,wu.realname,p.product_name, e.title,e.position,e.online_progress,e.finalResult,e.pmphAudit,e.expert_type, (case when e.online_progress=1 then '待审核' when e.online_progress=2 then '已退回' when e.online_progress=3 then '已通过' when e.online_progress=4 then '出版社退回' when e.online_progress=5 then '出版社退回' END) as xxsh, (case when e.online_progress=1 then '待学校审核' when ((e.online_progress=3) && (e.pmphAudit = 0)) then '待审核' when ((e.online_progress=1||e.online_progress=3) && (e.finalResult = 0)) then '正在遴选' when (e.online_progress=4 || e.online_progress=5) then '出版社退回' when (e.finalResult =1 && (e.pmphAudit!=1)) then '遴选结束' when (e.finalResult =1 && e.pmphAudit=1) then '报名成功' END) as cbssh, (case when e.isprint=0 then '未打印' when e.isprint=1 then '已打印' END) as isprint from expertation e left join product p on p.id=e.product_id left join writer_user wu on e.user_id=wu.id where e.online_progress !=0
and e.org_id=#{orgId}
and wu.realname like concat('%',#{queryName},'%' )
and e.expert_type =#{expertType}
and e.online_progress =1
and e.online_progress in(2,4,5)
and e.online_progress =3
limit #{start},#{pageSize}
