SELECT * FROM( SELECT d.realname AS NAME,d.gmt_create AS TIME,m.`material_name` AS TYPE FROM declaration d LEFT JOIN material m ON d.`material_id` = m.`id` LEFT JOIN org_user u ON d.`org_id` = u.`org_id`
u.`id` = #{userId} AND d.online_progress='1' AND d.gmt_create >= #{week} AND d.gmt_create >= #{month} AND d.gmt_create >= #{year}
ORDER BY d.gmt_create DESC) t1 UNION SELECT * FROM ( SELECT w1.username,w.gmt_create,"教师资格认证" FROM writer_user_certification w LEFT JOIN org_user u1 ON w.org_id = u1.org_id LEFT JOIN writer_user w1 ON w1.id= w.user_id
u1.`id` = #{userId} AND w.progress='1' AND w.gmt_create >= #{week} AND w.gmt_create >= #{month} AND w.gmt_create >= #{year}
ORDER BY w.gmt_create DESC) t2 limit #{startPage},#{endPage}

SELECT COUNT(*) FROM ( SELECT * FROM( SELECT d.realname AS NAME,d.gmt_create AS TIME,m.`material_name` AS TYPE FROM declaration d LEFT JOIN material m ON d.`material_id` = m.`id` LEFT JOIN org_user u ON d.`org_id` = u.`org_id`
u.`id` = #{userId} AND d.online_progress='1' AND d.gmt_create >= #{week} AND d.gmt_create >= #{month} AND d.gmt_create >= #{year}
ORDER BY d.gmt_create DESC) t1 UNION SELECT * FROM ( SELECT w1.username,w.gmt_create,"教师资格认证" FROM writer_user_certification w LEFT JOIN org_user u1 ON w.org_id = u1.org_id LEFT JOIN writer_user w1 ON w1.id= w.user_id
u1.`id` = #{userId} AND w.progress='1' AND w.gmt_create >= #{week} AND w.gmt_create >= #{month} AND w.gmt_create >= #{year}
ORDER BY w.gmt_create DESC) t2)t3

select msg_id,gmt_create,title 
from user_message 
where receiver_id = #{userId} 
ORDER BY gmt_create DESC
limit #{startPage},#{endPage}

select gmt_create,title,msg_id 
from user_message 
where receiver_id = #{userId} 
AND gmt_create like #{time} 
ORDER BY gmt_create DESC
limit #{startPage},#{endPage}
