SELECT * FROM( SELECT d.id as ID,d.realname AS NAME,d.gmt_create AS TIME,m.`material_name` AS CONTENT,d.material_id as auditId,"A" as TYPE,w.avatar,d.online_progress as online_progress,d.material_id AS materialId FROM declaration d LEFT JOIN material m ON d.`material_id` = m.`id` LEFT JOIN org_user u ON d.`org_id` = u.`org_id` left join writer_user w on d.user_id = w.id
u.`id` = #{parameter.userId} AND (d.online_progress='1' or d.online_progress='4') and m.is_all_textbook_published = 0 and m.is_force_end = 0 and DATE_FORMAT(m.actual_deadline,'%Y%m%d') >= '20180412' AND d.gmt_create >= #{parameter.week} AND d.gmt_create >= #{parameter.month} AND d.gmt_create >= #{parameter.year}
ORDER BY d.gmt_create DESC) t1 UNION SELECT * FROM ( SELECT w1.org_id,w1.realname,w.gmt_create,"教师资格认证",w1.id,"B",w1.avatar, '8' as online_progress ,null AS materialId FROM writer_user_certification w LEFT JOIN org_user u1 ON w.org_id = u1.org_id LEFT JOIN writer_user w1 ON w1.id= w.user_id
u1.`id` = #{parameter.userId} AND w.progress='1' AND w.gmt_create >= #{parameter.week} AND w.gmt_create >= #{parameter.month} AND w.gmt_create >= #{parameter.year}
ORDER BY w.gmt_create DESC) t2 union SELECT * FROM ( SELECT a.id as ID, (CASE WHEN a.sender_type='0' THEN '系统消息' WHEN a.sender_type='1' THEN r1.realname WHEN a.sender_type='2' THEN s2.realname WHEN a.sender_type='3' THEN s3.realname END ) NAME, a.gmt_create AS TIME, msg_id AS CONTENT, a.sender_id as auditId, 'C' as TYPE, (CASE WHEN a.sender_type='0' THEN 'DEFAULT' WHEN a.sender_type='1' THEN r1.avatar WHEN a.sender_type='2' THEN s2.avatar WHEN a.sender_type='3' THEN s3.avatar END ) avatar, a.is_read as online_progress,a.material_id AS materialId FROM user_message a LEFT JOIN writer_user s2 ON a.sender_id = s2.id AND a.sender_type = 2 LEFT JOIN org_user s3 ON a.sender_id = s3.id AND a.sender_type = 3 LEFT JOIN pmph_user r1 ON a.sender_id = r1.id AND a.sender_type = 1 WHERE receiver_type = 3 and a.is_withdraw = 0 and a.is_read = 0 AND a.is_deleted=0 AND a.msg_type IN (1, 0) AND a.gmt_create >= #{parameter.week} AND a.gmt_create >= #{parameter.month} AND a.gmt_create >= #{parameter.year} AND receiver_id = #{parameter.userId} ) t3
union
select * from( select e.id as ID ,e.realname as NAME ,e.gmt_create as TIME ,p.product_name as CONTENT ,p.id as auditId ,'D' as TYPE ,w.avatar as avatar ,e.online_progress as online_progress,null AS materialId
from expertation e inner join org_user u on e.org_id = u.org_id and u.id = #{parameter.userId} AND (e.online_progress='1' or e.online_progress='4') left join writer_user w on e.user_id = w.id left join product p on e.product_id = p.id
AND e.gmt_create >= #{parameter.week} AND e.gmt_create >= #{parameter.month} AND e.gmt_create >= #{parameter.year}
)t4
ORDER BY TIME DESC limit #{start},#{pageSize}

SELECT COUNT(*) FROM ( SELECT * FROM( SELECT d.realname AS NAME,d.gmt_create AS TIME,m.`material_name` AS Content,"A" as TYPE FROM declaration d LEFT JOIN material m ON d.`material_id` = m.`id` LEFT JOIN org_user u ON d.`org_id` = u.`org_id`
u.`id` = #{parameter.userId} AND (d.online_progress='1' or d.online_progress='4') and m.is_all_textbook_published = 0 and m.is_force_end = 0 and DATE_FORMAT(m.actual_deadline,'%Y%m%d') >= '20180412' AND d.gmt_create >= #{parameter.week} AND d.gmt_create >= #{parameter.month} AND d.gmt_create >= #{parameter.year}
ORDER BY d.gmt_create DESC) t1 UNION SELECT * FROM ( SELECT w1.realname,w.gmt_create,"教师资格认证","B" FROM writer_user_certification w LEFT JOIN org_user u1 ON w.org_id = u1.org_id LEFT JOIN writer_user w1 ON w1.id= w.user_id
u1.`id` = #{parameter.userId} AND w.progress='1' AND w.gmt_create >= #{parameter.week} AND w.gmt_create >= #{parameter.month} AND w.gmt_create >= #{parameter.year}
ORDER BY w.gmt_create DESC) t2 union SELECT * FROM ( SELECT (CASE WHEN a.sender_type='0' THEN '系统消息' WHEN a.sender_type='1' THEN a.title END ) NAME, DATE_FORMAT( a.gmt_create, '%Y-%m-%d %H:%i:%S' ) AS TIME, msg_id AS CONTENT, 'C' as TYPE FROM user_message a LEFT JOIN writer_user s2 ON a.sender_id = s2.id AND a.sender_type = 2 LEFT JOIN org_user s3 ON a.sender_id = s3.id AND a.sender_type = 3 LEFT JOIN pmph_user r1 ON a.sender_id = r1.id AND a.sender_type = 1 WHERE receiver_type = 3 and a.is_withdraw = 0 and a.is_read = 0 AND a.is_deleted=0 AND a.msg_type IN (1, 0) AND a.gmt_create >= #{parameter.week} AND a.gmt_create >= #{parameter.month} AND a.gmt_create >= #{parameter.year} AND receiver_id = #{parameter.userId} ) t3
union
select * from( select
e.realname as NAME ,e.gmt_create as TIME ,p.product_name as CONTENT ,'D' as TYPE
from expertation e inner join org_user u on e.org_id = u.org_id and u.id = #{parameter.userId} AND (e.online_progress='1' or e.online_progress='4') left join writer_user w on e.user_id = w.id left join product p on e.product_id = p.id
AND e.gmt_create >= #{parameter.week} AND e.gmt_create >= #{parameter.month} AND e.gmt_create >= #{parameter.year}
)t4 )t5

select msg_id,gmt_create,title 
from user_message 
where receiver_id = #{userId} 
ORDER BY gmt_create DESC
limit #{startPage},#{endPage}

select gmt_create,title,msg_id 
from user_message 
where receiver_id = #{userId} 
AND gmt_create like #{time} 
ORDER BY gmt_create DESC
limit #{startPage},#{endPage}
