mapper.select:
SELECT dp.id as dec_position_id, d.user_id, d.realname, d.sex, d.org_name AS work_org_name, d.org_id as dec_org_id, (case when d.org_id = 0 then '人民卫生出版社' else o.org_name end) AS dec_org_name, d.position, (dp.chosen_position NOT IN (2, 4, 10, 12) and dpp.chosen_position NOT IN (2, 4, 10, 12)) as editorAvaliable, d.title, tb.is_list_selected, tb.is_locked, m.is_digital_editor_optional, m.is_force_end, (case when tb.is_published = 1 or m.is_force_end =1 or #{parameter.isFirstEditorLogIn} = 'false' then dpp.chosen_position when tb.is_list_selected = 1 or tb.is_locked =1 then dp.chosen_position else dpt.chosen_position end) as chosen_position
FROM declaration AS d Inner Join dec_position AS dp ON dp.declaration_id = d.id left join textbook tb on tb.id = dp.textbook_id left Join dec_position_temp AS dpt ON dpt.declaration_id = dp.declaration_id and dpt.textbook_id=dp.textbook_id
and dpt.author_id = #{parameter.logUserId}
left Join dec_position_published AS dpp ON dpp.declaration_id = dp.declaration_id and dpp.textbook_id=dp.textbook_id
inner join material m on m.id = tb.material_id
Left Join org AS o ON d.org_id = o.id
and dp.textbook_id = ${parameter.textBookId}
and d.realname like concat("%",#{parameter.queryName},"%")
and o.id = ${parameter.queryOrg}
limit ${start},${pageSize}

SELECT dp.id as dec_position_id, tb.is_list_selected , (case when tb.is_published = 1 or m.is_force_end =1 or #{parameter.isFirstEditorLogIn} = 'false' then dpp.chosen_position when tb.is_list_selected = 1 or tb.is_locked =1 then dp.chosen_position else dpt.chosen_position end) as chosen_position FROM declaration AS d left Join dec_position AS dp ON dp.declaration_id = d.id left join textbook tb on tb.id = dp.textbook_id left Join dec_position_temp AS dpt ON dpt.declaration_id = dp.declaration_id and dpt.textbook_id=dp.textbook_id
and dpt.author_id = #{parameter.logUserId}
left Join dec_position_published AS dpp ON dpp.declaration_id = dp.declaration_id and dpp.textbook_id=dp.textbook_id
inner join material m on m.id = tb.material_id
Left Join org AS o ON d.org_id = o.id
and dp.textbook_id = ${parameter.textBookId}
and d.realname like concat("%",#{parameter.queryName},"%")
and o.id = ${parameter.queryOrg}

SELECT dp.id as dec_position_id, tb.is_list_selected, case when tb.is_list_selected = 1 then dp.chosen_position else dpt.chosen_position end as chosen_position FROM dec_position AS dp left join textbook tb on tb.id = dp.textbook_id left Join dec_position_temp AS dpt ON dpt.declaration_id = dp.declaration_id and dpt.textbook_id=dp.textbook_id
and dpt.author_id = #{parameter.logUserId}
Inner Join declaration AS d ON dp.declaration_id = d.id Left Join org AS o ON d.org_id = o.id
case when tb.is_list_selected = 1 then dp.chosen_position >= 8 else dpt.chosen_position >= 8 end
and dp.textbook_id = ${parameter.textBookId}
and d.realname like concat("%",#{parameter.queryName},"%")
and o.id = ${parameter.queryOrg}

mapper.insert:
insert into dec_position_temp ( id, author_id, is_background, declaration_id, textbook_id, preset_position, is_on_list, chosen_position, rank, syllabus_id, syllabus_name ) select d.id, ${logUserId}, ${is_background}, d.declaration_id, d.textbook_id, d.preset_position, d.is_on_list, 1, d.rank, d.syllabus_id, d.syllabus_name
from dec_position d where d.id in (${selectedIds}) and d.id not in (${selectedNumIds}) ON DUPLICATE KEY UPDATE author_id= values(author_id) , is_background = values(is_background) , gmt_update = now();
insert into dec_position_temp ( id, author_id, is_background, declaration_id, textbook_id, preset_position, is_on_list, chosen_position, rank, syllabus_id, syllabus_name ) select d.id, ${logUserId}, ${is_background}, d.declaration_id, d.textbook_id, d.preset_position, d.is_on_list, (case when d.chosen_position in (1,9) then 8 when d.chosen_position < 8 then d.chosen_position+8 else d.chosen_position end )as chosen_position, d.rank, d.syllabus_id, d.syllabus_name
from dec_position d where d.id not in (${selectedIds}) and d.id in (${selectedNumIds}) ON DUPLICATE KEY UPDATE author_id= values(author_id) , is_background = values(is_background) , gmt_update = now();
insert into dec_position_temp ( id, author_id, is_background, declaration_id, textbook_id, preset_position, is_on_list, chosen_position, rank, syllabus_id, syllabus_name ) select d.id, ${logUserId}, ${is_background}, d.declaration_id, d.textbook_id, d.preset_position, d.is_on_list, 9, d.rank, d.syllabus_id, d.syllabus_name
from dec_position d where d.id in (${selectedIds}) and d.id in (${selectedNumIds}) ON DUPLICATE KEY UPDATE author_id= values(author_id) , is_background = values(is_background) , gmt_update = now();
insert into dec_position_temp ( id, author_id, is_background, declaration_id, textbook_id, preset_position, is_on_list, chosen_position, rank, syllabus_id, syllabus_name ) select d.id, ${logUserId}, ${is_background}, d.declaration_id, d.textbook_id, d.preset_position, d.is_on_list, (case when d.chosen_position in (1,9) then 0 when d.chosen_position >= 8 then d.chosen_position -8 else d.chosen_position end ), d.rank, d.syllabus_id, d.syllabus_name
from dec_position d where d.textbook_id = #{textBookId} and d.id not in (${selectedIds}) and d.id not in (${selectedNumIds}) ON DUPLICATE KEY UPDATE author_id= values(author_id) , is_background = values(is_background) , gmt_update = now();
