mapper.select:
SELECT u.realname, u.position, u.title, u.email, u.is_teacher, c.id, c.user_id, c.org_id, c.handphone, c.idcard, c.progress, c.cert, u.avatar
FROM writer_user_certification AS c Inner Join writer_user AS u ON c.user_id = u.id inner join org_user as o on c.org_id = o.org_id
c.progress != 0
and o.id =#{parameter.orgUserId}
and u.realname like concat("%",#{parameter.queryName},"%")
and c.progress = ${parameter.queryStatus}
limit ${start},${pageSize}

select count(*) from writer_user_certification AS c Inner Join writer_user AS u ON c.user_id = u.id inner join org_user as o on c.org_id = o.org_id
c.progress != 0
and o.id =#{parameter.orgUserId}
and u.realname like concat("%",#{parameter.queryName},"%")
and c.progress = ${parameter.queryStatus}
