mapper.select:
select n.* from (select t.* from cms_content t left join material b on t.material_id =b.id where t.category_id=3 and t.material_id is not null and t.is_published=1 and t.material_id >0 and b.is_public = 0 and t.auth_status=2 and t.is_deleted=0 union select t.* from cms_content t left join material b on t.material_id =b.id left join material_org u on b.id=u.material_id left join writer_user p on p.org_id=u.org_id where t.category_id=3 and t.material_id is not null and t.is_published=1 and t.material_id >0 and b.is_public = 1 and t.auth_status=2 and t.is_deleted=0 and p.id= #{id}) n
where n.title like #{searchText}
order by n.gmt_create desc limit #{startnum},#{size}
