mapper.select:
select g.*, t.textbook_name bookName from pmph_group g LEFT JOIN textbook t ON g.book_id = t.id where g.id in ( select DISTINCT group_id from pmph_group_member where user_id = #{id} and is_writer = true AND is_deleted = FALSE )
order by rand()
order by #{order}
limit #{start},#{pageSize}

select * from pmph_group_member where
group_id=#{groupId}
and is_deleted = false
order by is_founder DESC ,is_admin DESC;

