mapper.select:
SELECT *, m.gmt_create sendTime FROM user_message m WHERE m.receiver_id=#{parameter.userId} AND m.receiver_type = #{parameter.userType} AND m.is_deleted = false AND m.is_withdraw = false AND m.msg_type = 2
and m.title like concat('%',#{parameter.title},'%')
and m.is_read = #{parameter.isRead}
ORDER BY m.gmt_create DESC LIMIT #{start},#{pageSize}

mapper.update:
update user_message set is_deleted = true where id in
#{id}
;
