mapper.select:
select a.id as receiver_id,'orgUserId' as type from org_user a,org_user b where a.org_id = b.org_id and a.id=#{userId}
union all
select d.id as receiver_id, 'writerUserId' as type from org_user c join writer_user d on c.org_id = d.org_id where c.id= #{userId}

SELECT a.title,m.`deadline`,m.`mail_address`,m.is_force_end,m.is_all_textbook_published,a.mid as mongoId,m.deadline,m.actual_deadline, a.is_material_entry,a.material_id,e.`note`,e.notice,e.id AS attachmentId , (case when date_format(CONCAT(date_format(m.actual_deadline, '%Y%m%d'),'235959'),'%Y-%m-%d %H:%i:%s')>NOW() then 'no' else 'yes' end) as ended
,(select case when count(d.id) >0 then 'yes' else 'no' end from declaration d where d.material_id=a.`material_id` and d.user_id=#{userid})as isapply
FROM cms_content a LEFT JOIN material m ON a.`material_id` = m.`id` LEFT JOIN material_extra e ON m.`id` = e.`material_id` WHERE a.`id`=#{cmsId}


select * from `material_note_attachment` where material_extra_id in(
   select me.id from material_extra me left join  material m on m.id=me.material_id where m.id=#{materialId}
)

select a.id ,a.msg_id ,a.title,a.msg_type,a.gmt_create,
( CASE WHEN a.sender_type = 1 THEN s1.realname WHEN a.sender_type = 2 THEN s2.realname WHEN a.sender_type = 3 THEN s3.realname ELSE '系统' END ) realname
FROM user_message a LEFT JOIN pmph_user s1 ON a.sender_id = s1.id AND a.sender_type = 1 LEFT JOIN writer_user s2 ON a.sender_id = s2.id AND a.sender_type = 2 LEFT JOIN org_user s3 ON a.sender_id = s3.id AND a.sender_type = 3 where a.id=#{id}
