mapper.select:
select t1.id,t1.user_id,t1.bookname,SUBSTR(t1.deadline,1,10) deadline, t1.source,t1.word_number,t1.picture_number,t1.subject,t1.rank,t1.type,t1.phone, t1.bank_account_id,t1.purchase,t1.sponsorship,t1.is_translation,t1.original_bookname, t1.original_author,t1.nation,t1.edition,t1.auth_progress,t1.auth_date,t1.auth_feedback, t1.is_opts_handling,t1.opts_role_id,t1.is_rejected_by_director,t1.reason_director,t1.is_director_handling,t1.department_id, t1.is_rejected_by_editor,t1.reason_editor,t1.is_editor_handling,t1.editor_id,t1.is_accepted,t1.is_staging,t1.is_deleted, t1.note,t1.gmt_create,t1.gmt_update,t1.reader,t1.tn,t1.vn,t1.submit_time,t1.reader_quantity,t1.campaign,t1.sales_channel, t1.lifecycle,t1.print_advise,t1.price_advise,t1.print_number,t1.cost,t1.min_print_number,t1.benefit, t1.is_revision,t1.revision_bookname,t1.revision_author,SUBSTR(t1.revision_publish_date,1,10) revision_publish_date,t1.revision_print,t1.revision_stock, t1.realname,t1.sex,t1.price,t1.position,t1.position_profession,t1.degree,t1.workplace,t1.address,t1.postcode,t1.email from topic t1 where 1= 1
and t1.vn = #{vn}
and t1.id = #{topic_id}

select id,topic_id,realname,sex*1 sex,price,position,workplace,phone,degree from
topic_writer t1 where 1= 1

	and t1.topic_id = #{topic_id}

select * from writer_bank_account t where 1=1

	and t.id = #{bank_account_id}


	and t.account_number = #{account_number}


	and t.user_id = #{user_id}

order by gmt_create desc

mapper.update:
update writer_bank_account
set account_number =#{account_number},bank=#{bank}
where id = #{bank_account_id}

mapper.insert:
		INSERT INTO topic_similar_book
		(topic_id, bookname, edition, author, booksize, publisher, print_number, price, publish_date)
 		VALUES ( #{topic_id}, #{bookname}, #{edition}, #{author}, #{booksize}, #{publisher}, #{print_number}, #{price}, #{publish_date});
